<?php

namespace AppBundle\Repository;
use AppBundle\Criteria\IssueCriteria;
use AppBundle\Entity\Project;
use AppBundle\Entity\WebsiteConfig;
use Doctrine\Common\Collections\Criteria;
use Doctrine\ORM\EntityRepository;

/**
 * IssueRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class IssueRepository extends EntityRepository implements BaseRepository
{

    public function countCurrentIssues(Project $project)
    {

    }

    public function indexIssues(WebsiteConfig $config)
    {
        $qb = $this->createQueryBuilder('i');
        return $qb->leftJoin('i.status','status')
            ->orderBy('status.id','ASC')
            ->where('i.fixVersion = :version')
            ->setParameter('version', $config->getCurrentVersion())
            ->getQuery()
            ->getResult()
        ;
    }

    /**
     * @param $criteria
     *
     * @return array<Issue>|array<array>
     ***/
    public function match($criteria)
    {
        /** @var IssueCriteria $criteria */
        $qb = $this->createQueryBuilder('u');

        if ($criteria->version !== null) {
            $this->matchVersion($qb, $criteria);
        }

        if($criteria->version !== null) {
            $this->matchStatus($qb, $criteria);
        }

        if($criteria->sortBy !== null) {
            $this->matchSort($qb, $criteria);
        }

        return $qb->getQuery()->getResult($criteria->hydrateMode);
    }

    private function matchVersion($qb, IssueCriteria $criteria)
    {
        $qb->andWhere('u.fixVersion = :version')->setParameter('version', $criteria->version);
    }

    private function matchStatus($qb, IssueCriteria $criteria)
    {
        $qb->andWhere('u.status = :status')->setParameter('status', $criteria->status);
    }
}
